mapper.select:
SELECT COUNT(*) FROM cart_tbl
WHERE user_id = #{user_id}
AND item_code = #{item_code}
AND item_color = #{item_color}
AND item_size = #{item_size}

mapper.update:
UPDATE cart_tbl
SET quantity = quantity + #{quantity}
WHERE user_id = #{user_id}
AND item_code = #{item_code}
AND item_color = #{item_color}
AND item_size = #{item_size}
