mapper.select:
SELECT * FROM (
    SELECT rownum rn, a.*
    FROM (
        SELECT i.*, u.name 
        FROM items_tbl i
        JOIN user_info u ON i.user_id = u.user_id
    ) a
)
WHERE rn > #{start} AND rn < #{end}

SELECT * FROM (
    SELECT rownum rn, a.*
    FROM (
        SELECT i.*, u.name 
        FROM items_tbl i
        JOIN user_info u ON i.user_id = u.user_id
        WHERE i.item_id BETWEEN #{start} AND #{end} 
    ) a
)
WHERE rn > #{startnum} AND rn < #{endnum}

SELECT * FROM (
    SELECT rownum rn, a.*
    FROM (
        SELECT i.*, u.name 
        FROM items_tbl i
        JOIN user_info u ON i.user_id = u.user_id
        WHERE i.item_id = #{item_id} 
    ) a
)
WHERE rn > #{start} AND rn < #{end}

SELECT * FROM (
    SELECT rownum rn, a.* FROM (
        SELECT i.*, u.name
        FROM items_tbl i
        JOIN user_info u ON i.user_id = u.user_id
        WHERE i.item_title LIKE '%' || #{name} || '%'
           OR i.user_id LIKE '%' || #{name} || '%'
           OR u.name LIKE '%' || #{name} || '%'
    ) a
) WHERE rn > #{start} AND rn < #{end}
