mapper.update:
UPDATE snap_profile 
SET nickname= #{nickname}, intro= #{intro}, profile_img= #{profile_img}, height= #{height}, weight=#{weight}, gender=#{gender} 
WHERE user_id=#{user_id}

mapper.select:
	SELECT user_id, nickname, intro, profile_img, height, weight, gender 
	FROM snap_profile 
    WHERE user_id = #{userId}
